import React, { useState, useEffect } from "react";
import MaterialTable from "material-table";
import { TextField, MenuItem } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import AddIcon from "@material-ui/icons/Add";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
import CheckIcon from "@material-ui/icons/Check";
import SearchIcon from "@material-ui/icons/Search";
import LeaveForm from "./LeaveForm";
import { CustomDialog } from "../helpers/CustomDialog";
import { AlertDialog } from "../helpers/AlertDialogBox";
import { useExpensesStatus } from "./expenses/useExpensesStatus";
import { useEmployeesContext } from "../context/employees_context";

const columns = [
  {
    title: "January",
    field: "jan",
  },
  {
    title: "February",
    field: "feb",
  },
  {
    title: "March",
    field: "mar",
  },
  {
    title: "April",
    field: "apr",
  },
  {
    title: "May",
    field: "may",
  },
  {
    title: "June",
    field: "jun",
  },
  {
    title: "July",
    field: "jul",
  },
  {
    title: "August",
    field: "aug",
  },
  {
    title: "September",
    field: "sep",
  },
  {
    title: "October",
    field: "oct",
  },
  {
    title: "November",
    field: "nov",
  },
  {
    title: "December",
    field: "dec",
  },
];

const data=[{ jan: 80, feb: 90, mar: 78, apr: 63, may: 55, jun: 60, jul: 55, aug: 65, sep:70, oct: 58, nov: 60, dec:62 }]     

export default function ExpenseTableView({ status }) {
  const classes = useStyles();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [isAlertOpen, setIsAlertOpen] = useState(false);
  const { loadEmployees } = useEmployeesContext();
  const { expensesstatus, setExpStatusId} = useExpensesStatus()
  // const {
  //   //expenses,
  //   addExpense,
  //   expenses_loading,
  //   updateExpense,
  //   deleteExpense,
  //   loadPendingExpenses,
  //   getSingleExpense,
  //   setEditExpenseID,
  //   setIsExpenseEditingOn,
  //   setIsExpenseEditingOff,
  //   resetSingleExpense,
  // } = useExpensesContext();

  useEffect(() => {
    setExpStatusId(status);
  }, []);

  const handleDialogOpen = () => {
    setIsDialogOpen(true);
  };

  const handleDialogClose = () => {
    setIsDialogOpen(false);
    //loadPendingExpenses(FILTERSTRING);
  };

  const handleAlertOpen = () => {
    setIsAlertOpen(true);
  };

  const handleAlertClose = () => {
    setIsAlertOpen(false);
  };

  
  return (
    <div className={classes.root}>
      {/* <h1>Expenses Claims Application</h1> */}

      <div style={{ maxWidth: "100%", paddingTop: "5px" }}>
        <MaterialTable
          columns={columns}
          data={data}
          title="Expenses Claims"
          options={{
            filtering: false,
            search: false,
            toolbar: false,
            headerStyle: {
              backgroundColor: "rgba(75, 192, 192, 1)",
              color: "white",
            },
            showTitle: false,
          }}
        />
      </div>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
}));
