import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import axios from "axios";
import { Box, Container, Flex, Heading, Stack } from "@chakra-ui/react";
import { employees_url } from "../utils/constants";
import Calendar from "react-awesome-calendar";

const events = [
  {
    id: 1,
    color: "#fd3153",
    from: "2021-10-02T18:00:00+00:00",
    to: "2021-10-05T19:00:00+00:00",
    title: "This is an event",
  },
  {
    id: 2,
    color: "#1ccb9e",
    from: "2021-10-11T13:00:00+00:00",
    to: "2021-10-15T14:00:00+00:00",
    title: "This is another event",
  },
  {
    id: 3,
    color: "#3694DF",
    from: "2021-10-25T13:00:00+00:00",
    to: "2021-10-27T20:00:00+00:00",
    title: "This is also another event",
  },
];

const Example = () => {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const [paydata, setPaydata] = useState([]);
  const [loading, setLoading] = useState(true);
  const [jobs, setJobs] = useState([]);
  const [value, setValue] = useState(0);

  return (
    <Box
      display="block"
      h="600"
      w="container.xl"
      px="15"
      mt="100"
      overflow="scroll"
      border="1px solid red"
    >
      <Calendar
        events={events}
        header={{
          current: {
            year: "numeric",
            month: "numeric",
            day: "numeric",
          },
          mode: "dailyMode" || "monthlyMode" || "yearlyMode",
          prev: {
            year: numeric,
            month: numeric,
            day: numeric
          },
          next: {
            year: numeric,
            month: numeric,
            day: numeric
          },
          //onClickPrev: function - this will change the calendar to the prev year, month, day depending on the mode
          //onClickNext: function - this will change the calendar to the next year, month, day depending on the mode
        }}
      
      />
    </Box>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  container: {
    margin: 0,
    padding: 0,
    width: "80vw",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill,250px)",
    gridAutoRows: "10px",
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    justifyContent: "center",
    backgroundColor: "primary",
  },
  fixedHeight: {
    height: 800,
  },
  paper: {
    padding: theme.spacing(10),
    // display: "flex",
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
    border: "1px solid",
    width: "100%",
    color: "primary",
    bcakgroundColor: "black",
  },
  card: {
    backgroundColor: "black",
  },
  card_body: {
    padding: "0",
  },

  section: {
    width: "70vw",
    margin: "5rem auto",
    maxWidth: "var(--max-width)",
  },
  underline: {
    width: "5rem",
    height: "0.25rem",
    marginBottom: "1.25rem",
    background: "var(--clr-primary-5)",
    marginLeft: "auto",
    marginRight: "auto",
  },
  title: {
    marginbottom: "4rem",
    textAlign: "center",
  },
  jobscenter: {
    width: "80vw",
    margin: "0 auto",
    maxWidth: "var(--max-width)",
    flexDirection: "row",
  },
  btncontainer: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    marginBottom: "4rem",
    flexWrap: "wrap",
  },
  jobbtn: {
    background: "transparent",
    borderColor: "transparent",
    textTransform: "capitalize",
    fontSize: "1.25rem",
    letterSpacing: "var(--spacing)",
    margin: "0 0.5rem",
    transition: "var(--transition)",
    cursor: "pointer",
    padding: "0.25rem 0",
    lineHeight: "1",
    outlineColor: "var(--clr-primary-10)",
    "&:hover": {
      color: "var(--clr-primary-5)",
      boxShadow: "0 2px var(--clr-primary-5)",
    },
  },
  activebtn: {
    color: "var(--clr-primary-5)",
    boxShadow: "0 2px var(--clr-primary-5)",
  },
  jobinfo: {
    fontWeight: "400",
  },

  dash_widget_icon: {
    backgroundColor: "white",
    borderRadius: "100%",
    color: "#ff9b44",
    display: "inline-block",
    float: "left",
    fontSize: "30px",
    height: "60px",
    lineHeight: "60px",
    marginRight: "10px",
    textAlign: "center",
    width: "60px",
  },
  dash_widget_info: {
    textAlign: "right",
  },
}));

export default Example;
