import React, { useState, useEffect } from "react";
import MaterialTable, { MTableToolbar } from "material-table";
import { TextField, MenuItem } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Box, Button, HStack } from "@chakra-ui/react";
import AddIcon from "@material-ui/icons/Add";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
import CheckIcon from "@material-ui/icons/Check";
import SearchIcon from "@material-ui/icons/Search";
import LeaveForm from "./LeaveForm";
import { CustomDialog } from "../helpers/CustomDialog";
import { AlertDialog } from "../helpers/AlertDialogBox";
import { useLeavesPeriod } from "./leaves/useLeavesPeriod";
import Export2Excel from "./Export2Excel";

const FILTERSTRING = "Pending";

const columns = [
  {
    title: "Name",
    field: "name",
  },

  {
    title: "From Date",
    field: "from_date",
    type: "date",
    dateSetting: { locale: "en-GB" },
    editable: "never",
  },
  {
    title: "To Date",
    field: "to_date",
    type: "date",
    dateSetting: { locale: "en-GB" },
    editable: "never",
  },
  {
    title: "No of Days",
    field: "no_of_days",
    type: "numeric",
    editable: "never",
  },
  {
    title: "Reason",
    field: "reason",
    editable: "never",
  },
  { title: "Status", field: "status" },
];

export default function LeaveTableViewSummary({ year, month }) {
  const classes = useStyles();
  const { leavesperiod, setLeavePeriodYrId, setLeavePeriodMthId } =
    useLeavesPeriod();

  const Export_Leave = () => {
    Export2Excel()
  };
  
  
  
  useEffect(() => {
    setLeavePeriodYrId(year);
    setLeavePeriodMthId(month);
  }, []);

  return (
    <div className={classes.root}>
      {/* <h1>Expenses Claims Application</h1> */}

      {/* <div style={{ maxWidth: "100%", paddingTop: "5px" }}> */}
      <Box maxW="100%" pt="5px" overflow="scroll">
        <MaterialTable
          columns={columns}
          data={leavesperiod}
          title="Leave Application"
          options={{
            filtering: false,
            search: false,
            toolbar: false,
            paging: false,
            headerStyle: {
              backgroundColor: "rgba(75, 192, 192, 1)",
              color: "white",
            },
            showTitle: false,
          }}
          components={{
            Toolbar: (props) => (
              <div>
                <MTableToolbar {...props} />
                <HStack p={2}>
                  <Button
                    type="submit"
                    variant="solid"
                    color="white"
                    bgColor="red"
                    //className={classes.button}
                    onClick={Export_Leave}
                  >
                    Export
                  </Button>
                </HStack>
              </div>
            ),
          }}
        />
      </Box>
    </div>
    // </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 0,
  },
}));
