import React from "react";
import { Bar, char } from "react-chartjs-2";
import { Box } from "@chakra-ui/react";

const data = {
  labels: [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ],
  datasets: [
    {
      label: "Basic Salary",
      backgroundColor: "teal",
      borderColor: "rgba(255,99,132,1)",
      borderWidth: 1,
      stack: 1,
      hoverBackgroundColor: "rgba(255,99,132,0.4)",
      hoverBorderColor: "rgba(255,99,132,1)",
      data: [65, 59, 80, 81, 56, 55, 40, 55, 50, 65, 70, 65],
    },
    {
      label: "Site Allowances",
      backgroundColor: "skyblue",
      borderColor: "rgba(255,99,132,1)",
      borderWidth: 1,
      stack: 1,
      hoverBackgroundColor: "rgba(255,99,132,0.4)",
      hoverBorderColor: "rgba(255,99,132,1)",
      data: [25, 29, 10, 41, 16, 25, 20, 25, 23, 15, 24, 22],
    },
    {
      label: "Allowances",
      backgroundColor: "skyblue",
      borderColor: "rgba(255,99,132,1)",
      borderWidth: 1,
      stack: 1,
      hoverBackgroundColor: "rgba(255,99,132,0.4)",
      hoverBorderColor: "rgba(255,99,132,1)",
      data: [25, 29, 10, 41, 16, 25, 20, 25, 23, 15, 24, 22],
    },
    {
      label: "Deductions",
      backgroundColor: "cyan",
      borderColor: "rgba(255,99,132,1)",
      borderWidth: 1,
      stack: 1,
      hoverBackgroundColor: "rgba(255,99,132,0.4)",
      hoverBorderColor: "rgba(255,99,132,1)",
      data: [25, 39, 10, 41, 16, 25, 20, 16, 25, 35, 25, 33],
    },
  ],
};

export default function BarChartStack() {
  const options = {
    responsive: true,
    legend: {
      display: false,
    },
    type: "bar",
    scales: {
      xAxes: [
        {
          stacked: true,
        },
      ],
      yAxes: [
        {
          stacked: true,
        },
      ],
    },
  };
  return (
    <Box>
      <Bar data={data} width={1200} height="900" options={options} />
    </Box>
  );
}
