import React, { useState } from "react";
import {
  Button,
  Icon,
  TextField,
  Paper,
  Typography,
  Grid,
  Divider,
  Checkbox,
  FormControlLabel,
} from "@material-ui/core";

import { Alert } from "@material-ui/lab";
import { makeStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import { useEmployeesContext } from "../context/employees_context";
import { Controller, useForm } from "react-hook-form";
import { useRecoilState } from "recoil";
import { loginLevelState } from "./data/atomdata";
import { useAddEmployees } from "./employees/useAddEmployees";
import { useUpdateEmployees } from "./employees/useUpdateEmployees";
import { useDepartments } from "./departments/useDepartments";
import { useDesignations } from "./designations/useDesignations";
import App from "../utils/firebase";

// import EmpFamily from "./EmpFamily";
// import EmpEducations from "./EmpEducations";
// import EmpExperiences from "./EmpExperiences";
// import EmpTrainings from "./EmpTrainings";

const initial_values = {
  name: "",
  gender: "",
  ic_no: "",
  email: "",
  age: 0,
  birthdate: null,
  address: "",
  nationality: "",
  basic_salary: 0,
  currency: "BND",
  bank_name: "",
  bank_acno: "",
  tap_checkbox: true,
  tap_acno: "",
  scp_acno: "",
  date_of_join: null,
  date_of_resign: null,
  leave_bal:0,
  leave_entitled:0,
  designation: "",
  department: "",
  passportno: "",
  passport_expirydate: null,
  workpermitno: "",
  workpermit_expirydate: null,
  siteallows_fee: 0,
  perdiem_fee: 0,
  role: 1,
  password: "abc123*",
  empno: "",
};

const EmployeeFormNew = () => {
  const classes = useStyles();
  const {
    isEditing,
    single_employee,
    updateEmployee,
    //addEmployee,
    editEmployeeID,
    single_employee_loading,
  } = useEmployeesContext();
  const {
    name,
    ic_no,
    gender,
    age,
    birthdate,
    email,
    address,
    nationality,
    basic_salary,
    currency,
    bank_name,
    bank_acno,
    tap_checkbox,
    tap_acno,
    scp_acno,
    leave_bal,
    leave_entitled,
    password,
    date_of_join,
    date_of_resign,
    designation,
    department,
    passportno,
    passport_expirydate,
    workpermitno,
    workpermit_expirydate,
    siteallows_fee,
    perdiem_fee,
  } = single_employee || initial_values;
  const addEmployees = useAddEmployees();
  const updateEmployees = useUpdateEmployees();
  const { designations } = useDesignations();
  const { departments } = useDepartments();
  //const [alert, setAlert] = useState(false);
  const { handleSubmit, control } = useForm();
  const [loginLevel, setLoginLevel] = useRecoilState(loginLevelState);
  console.log("emplevel", loginLevel);
  const onSubmit = (data) => {
    if (isEditing) {
      updateEmployees({ id: editEmployeeID, ...data });
    } else {
      addEmployees({ password: "abc123*", role: 1, ...data });
      try {
        App.auth().createUserWithEmailAndPassword(email, "abc123*");
      } catch (error) {
        console.log(error);
      }
    }
    // setAlert(true);
    // setTimeout(() => {
    //   setAlert(false);
    // }, 3000);
    //loadEmployees();
    // <Alert severity="success">
    //   <AlertTitle>Success</AlertTitle>
    //   This is a success alert â€” <strong>check it out!</strong>
    // </Alert>;
    //history.push("/allemployees");
  };

  if (single_employee_loading) {
    return <div>Loading...</div>;
  }
  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        <Paper className={classes.root}>
          <Grid container className={classes.root} spacing={15}>
            <div>
              <Typography variant="h5" component="h3">
                EMPLOYEE FORM
              </Typography>

              <Button
                type="submit"
                variant="contained"
                color="primary"
                className={classes.button}
              >
                Submit <Icon className={classes.rightIcon}>send</Icon>
              </Button>
            </div>
          </Grid>
          <Divider className={classes.divider} />
          <Grid item xs={12}>
            <div>
              <div>
                <Controller
                  name="name"
                  control={control}
                  defaultValue={name}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Name"
                        id="margin-normal"
                        name="name"
                        defaultValue={name}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  rules={{ required: "Name required" }}
                />
              </div>
              <div>
                <Controller
                  name="email"
                  control={control}
                  defaultValue={email}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Email"
                        id="margin-normal"
                        name="email"
                        defaultValue={email}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  rules={{ required: "Email is required" }}
                />

                <Controller
                  name="ic_no"
                  control={control}
                  defaultValue={ic_no}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="IC No"
                        id="margin-normal"
                        name="ic_no"
                        defaultValue={ic_no}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  //rules={{ required: "IC No required" }}
                />
              </div>
              <div>
                <Controller
                  name="birthdate"
                  control={control}
                  defaultValue={birthdate}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Birth Date"
                        id="margin-birthdate"
                        name="birthdate"
                        type="date"
                        defaultValue={birthdate}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                        InputLabelProps={{
                          shrink: true,
                        }}
                      ></TextField>
                    );
                  }}
                  //rules={{ required: "IC No required" }}
                />

                <Controller
                  name="age"
                  control={control}
                  defaultValue={age}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Age"
                        type="number"
                        id="standard-number"
                        name="age"
                        defaultValue={age}
                        className={classes.textField}
                        //onChange={onChange}
                        onChange={(e) => {
                          onChange(parseInt(e.target.value, 10));
                        }}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  //rules={{ required: "IC No required" }}
                />
              </div>
              <div>
                <Controller
                  name="passportno"
                  control={control}
                  defaultValue={passportno}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Passport No"
                        id="margin-normal"
                        name="passportno"
                        defaultValue={passportno}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  //rules={{ required: "Name required" }}
                />
                <Controller
                  name="passport_expirydate"
                  control={control}
                  defaultValue={passport_expirydate}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Passport Expiry Date"
                        id="margin-normal"
                        name="passport_expirydate"
                        type="date"
                        defaultValue={passport_expirydate}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                        InputLabelProps={{
                          shrink: true,
                        }}
                      />
                    );
                  }}
                  //rules={{ required: "Name required" }}
                />
              </div>
              <div>
                <Controller
                  name="nationality"
                  control={control}
                  defaultValue={nationality}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Nationality"
                        id="standard-nationality"
                        name="nationality"
                        defaultValue={nationality}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  //rules={{ required: "IC No required" }}
                />
                <Controller
                  name="gender"
                  control={control}
                  defaultValue={gender}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Gender"
                        id="margin-normal"
                        name="gender"
                        defaultValue={gender}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                        select
                      >
                        <MenuItem value="Male">Male</MenuItem>
                        <MenuItem value="Female">Female</MenuItem>
                      </TextField>
                    );
                  }}
                  //rules={{ required: "IC No required" }}
                />
              </div>
              <div>
                <Controller
                  name="address"
                  control={control}
                  defaultValue={address}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Address"
                        id="standard-address"
                        name="address"
                        defaultValue={address}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  //rules={{ required: "IC No required" }}
                />
              </div>
            </div>
            <Divider className={classes.divider} />
            <div>
              <div>
                {loginLevel.loginLevel !== "Admin" && (
                  <Controller
                    name="basic_salary"
                    control={control}
                    defaultValue={basic_salary}
                    render={({
                      field: { onChange, value },
                      fieldState: { error },
                    }) => {
                      return (
                        <TextField
                          label="Basic Salary"
                          type="number"
                          id="standard-number"
                          name="basic_pay"
                          defaultValue={basic_salary}
                          className={classes.textField}
                          //onChange={onChange}
                          onChange={(e) => {
                            onChange(parseInt(e.target.value, 10));
                          }}
                          error={!!error}
                          helperText={error ? error.message : null}
                        />
                      );
                    }}
                    //rules={{ required: "IC No required" }}
                  />
                )}
              </div>
              <div>
                {loginLevel.loginLevel !== "Admin" && (
                  <Controller
                    name="siteallows_fee"
                    control={control}
                    defaultValue={siteallows_fee}
                    render={({
                      field: { onChange, value },
                      fieldState: { error },
                    }) => {
                      return (
                        <TextField
                          label="Site Allowances Fee"
                          type="number"
                          id="standard-number"
                          name="siteallows_fee"
                          defaultValue={siteallows_fee}
                          className={classes.textField}
                          //onChange={onChange}
                          onChange={(e) => {
                            onChange(parseInt(e.target.value, 10));
                          }}
                          error={!!error}
                          helperText={error ? error.message : null}
                        />
                      );
                    }}
                    //rules={{ required: "IC No required" }}
                  />
                )}
                {loginLevel.loginLevel !== "Admin" && (
                  <Controller
                    name="perdiem_fee"
                    control={control}
                    defaultValue={perdiem_fee}
                    render={({
                      field: { onChange, value },
                      fieldState: { error },
                    }) => {
                      return (
                        <TextField
                          label="Per Diem Fee"
                          type="number"
                          id="standard-number"
                          name="perdiem_fee"
                          defaultValue={perdiem_fee}
                          className={classes.textField}
                          //onChange={onChange}
