                </NativeSelect>
              </div>
            </Grid>
            <Grid
              item
              sm={4}
              align="center"
              style={{ border: "1px solid white" }}
            >
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type1amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type1amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type2amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type2amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type3amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type3amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type4amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type4amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type5amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type5amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type6amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type6amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type7amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type7amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
              <div>
                <TextField
                  label="Amount"
                  name="deducts_type8amt"
                  variant="filled"
                  type="number"
                  value={state.deducts_type8amt}
                  onChange={handleChange}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </div>
            </Grid>
          </Grid>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          {/* <div>
            <h3>{state.name}</h3>
          </div> */}
          <Divider variant="fullWidth" className={classes.divider} />
          <div>
            <TextField
              label="Wages"
              name="wages"
              variant="filled"
              type="number"
              value={currency(state.wages)}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="TAP Amount"
              name="tap_amount"
              variant="filled"
              type="number"
              value={currency(state.tap_amount)}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
            {/* </div>
          <div> */}
            <TextField
              label="SCP Amount"
              name="scp_amount"
              variant="filled"
              type="number"
              value={currency(state.scp_amount)}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Site Allowances"
              name="siteallows"
              variant="filled"
              type="number"
              value={currency(state.allows_type1amt)}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Expenses Claims"
              name="expclaims"
              variant="filled"
              type="number"
              value={currency(state.allows_type2amt)}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Allowances"
              name="total_allowances"
              variant="filled"
              type="number"
              value={currency(state.total_allowances)}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
          <div>
            <TextField
              label="Total Deductions"
              name="total_deductions"
              variant="filled"
              type="number"
              value={currency(state.total_deductions)}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
            {/* <CurrencyTextField
              label="Total Deductions"
              variant="standard"
              value={state.total_deductions}
              currencySymbol="$"
              outputFormat="string"
              decimalCharacter="."
              digitGroupSeparator=","
              decimalPlaces="2"
              className={classes.textField}
              id="standard-totaldeductions"
              name="total_deductions"
              InputProps={{
                readOnly: true,
              }}
            /> */}
          </div>
          <div>
            <TextField
              label="Nett Pay"
              name="nett_pay"
              variant="filled"
              type="number"
              value={currency(state.nett_pay)}
              onChange={handleChange}
              style={{ width: "100%" }}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                readOnly: true,
              }}
            />
          </div>
          {/* <div>
            <FormControlLabel
              control={
                <Checkbox
                  checked={state.tap_checkbox}
                  onChange={handleChange}
                  name="tap_checkbox"
                />
              }
              label="TAP/SCP Contribution"
            />
          </div> */}
          <div>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              className={classes.button}
              style={{ marginLeft: 10 }}
              onClick={handleCalc}
            >
              Calc <Icon className={classes.rightIcon}>send</Icon>
            </Button>
          </div>
        </Grid>
        {/* <button>Submit</button> */}
      </Grid>
    </form>
  );
};

