import React, { useState, useEffect } from "react";
import {
  Button,
  ButtonGroup,
  Grid,
  Icon,
  TextField,
  FormControlLabel,
  Divider,
  ListSubheader,
  MenuItem,
  Select,
  NativeSelect,
  InputLabel,
} from "@material-ui/core";
import { Box, Checkbox, Heading, Text } from "@chakra-ui/react";
import currency from "currency.js";
import CurrencyTextField from "@unicef/material-ui-currency-textfield";
import { Autocomplete } from "@material-ui/lab";
import { makeStyles } from "@material-ui/core/styles";
import { usePayslipsContext } from "../context/payslips_context";

const initial_state = [
  {
    name: "",
    period: "",
    date: "",
    payrun: "",
    nett_pay: 0,
    bank_name: "",
    bank_acno: "",
    tap_acno: "",
    tap_amount: 0,
    scp_acno: "",
    scp_amount: 0,
    total_allowances: 0,
    total_deductions: 0,
    empid: "",
    status: "",
    allows_type1: " ",
    allows_type1amt: 0,
    allows_type2: " ",
    allows_type2amt: 0,
    allows_type3: " ",
    allows_type3amt: 0,
    allows_type4: " ",
    allows_type4amt: 0,
    allows_type5: " ",
    allows_type5amt: 0,
    allows_type6: " ",
    allows_type6amt: 0,
    allows_type7: "",
    allows_type7amt: 0,
    allows_type8: "",
    allows_type8amt: 0,
    deducts_type1: "",
    deducts_type1amt: 0,
    deducts_type2: " ",
    deducts_type2amt: 0,
    deducts_type3: " ",
    deducts_type3amt: 0,
    deducts_type4: "",
    deducts_type4amt: 0,
    deducts_type5: "",
    deducts_type5amt: 0,
    deducts_type6: "",
    deducts_type6amt: 0,
    deducts_type7: "",
    deducts_type7amt: 0,
    deducts_type8: "",
    deducts_type8amt: 0,
    tap_checkbox: false,
    salary_currency: "",
    currency_rate: 1,
  },
];

const PayForm = ({
  formdata,
  setFormdata,
  loadFormdata,
  setLoadFormdata,
  payitems,
  setLoadUpdatedata,
  rowindex,
  isCalc,
  setIsCalc,
}) => {
  const classes = useStyles();
  const [state, setState] = useState(initial_state);
  const { singlebatchpayslip } = usePayslipsContext();

  useEffect(() => {
    setState(initial_state);
    setState({ ...formdata });
    setLoadFormdata(false);
    console.log("useeffect state", state);
  }, [loadFormdata]);

  // useEffect(() => {
  //   handleCalc()
  // },[state])

  const Update_Empdata = ({ name, value }) => {
    let data = singlebatchpayslip[rowindex];
    data[name] = value;
    console.log("update data", data);
    console.log("update payslip", singlebatchpayslip[rowindex]);
  };

  const handleChange = (e) => {
    e.preventDefault();
    const { name, type, value } = e.target;
    const val = type === "number" ? parseFloat(value) : value;
    //setFormInput({ [name]: val });
    setState({ ...state, [name]: val });
    Update_Empdata({ name: name, value: val });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log("submitform");
    handleCalc();
  };

  const handleCalc = (e) => {
    var totalTAP = 0,
      totalSCP = 0,
      nettPay = 0.0,
      allows = 0,
      deducts = 0,
      siteallows = 0,
      expsclaims = 0;
    let data = singlebatchpayslip[rowindex];
    totalTAP = state.tap_checkbox ? Math.ceil(state.wages * 0.05) : 0;
    totalSCP = state.tap_checkbox
      ? Math.round((state.wages + Number.EPSILON) * 0.035 * 100) / 100
      : 0;
    if (totalSCP > 98) {
      totalSCP = 98;
    }
    siteallows = parseFloat(state.allows_type1amt);
    expsclaims = parseFloat(state.allows_type2amt);
    allows =
      parseFloat(state.allows_type3amt) +
      parseFloat(state.allows_type4amt) +
      parseFloat(state.allows_type5amt) +
      parseFloat(state.allows_type6amt) +
      parseFloat(state.allows_type7amt) +
      parseFloat(state.allows_type8amt);

    deducts =
      parseFloat(state.deducts_type1amt) +
      parseFloat(state.deducts_type2amt) +
      parseFloat(state.deducts_type3amt) +
      parseFloat(state.deducts_type4amt) +
      parseFloat(state.deducts_type5amt) +
      parseFloat(state.deducts_type6amt) +
      parseFloat(state.deducts_type7amt) +
      parseFloat(state.deducts_type8amt);

    nettPay =
      state.wages +
      siteallows +
      expsclaims -
      totalTAP -
      totalSCP +
      allows -
      deducts;

    setState({
      ...state,
      total_allowances: allows,
      total_deductions: deducts,
      tap_amount: totalTAP,
      scp_amount: totalSCP,
      nett_pay: nettPay,
      wages_bnd:
        Math.round((wages + Number.EPSILON) * 0.035 * 100) / 100,
    });
    //update employee data
    data.tap_amount = totalTAP;
    data.scp_amount = totalSCP;
    data.total_allowances = allows;
    data.total_deductions = deducts;
    data.nett_pay = nettPay;
  };

  return (
    <form onSubmit={handleFormSubmit}>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Text as="u" fontSize="md">
            Allowances
          </Text>
        </Grid>

        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Text as="u" fontSize="md">
            Deductions
          </Text>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Text as="u" fontSize="md">
            Summary ({state.name})
          </Text>
        </Grid>
      </Grid>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Grid container direction="row" style={{ border: "1px solid white" }}>
            <Grid
              item
              sm={8}
              align="center"
              style={{ border: "1px solid white" }}
            >
              <Box>
                <Text fontSize="sm">Description</Text>
              </Box>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type1"
                  variant="filled"
                  type="text"
                  value={state.allows_type1}
                  onChange={handleChange}
                  style={{ width: "100%" }}
                  InputLabelProps={{
                    shrink: true,
                  }}
                  InputProps={{
                    readOnly: true,
                  }}
                ></TextField>
              </div>
              <div>
                <TextField
                  label="Allowance"
                  name="allows_type2"
                  variant="filled"
                  type="text"
                  displayEmpty
                  defaultValue=""
                  value={state.allows_type2}
                  onChange={handleChange}
                  style={{ width: "100%" }}
                  InputLabelProps={{
                    shrink: true,
                  }}
                  InputProps={{
                    readOnly: true,
                  }}
                ></TextField>
              </div>
              <div>
                <InputLabel
                  htmlFor="deduct-customized-native-simple"
                  className={classes.formLabel}
                >
                  Allowance
                </InputLabel>
                <NativeSelect
                  name="allows_type3"
                  value={state.allows_type3}
                  style={{
                    padding: 4,
                    marginLeft: 5,
                    width: "100%",
                    textAlign: "left",
                  }}
                  onChange={handleChange}
                >
                  <option value="">None</option>
                  {payitems
                    .filter(function (item) {
                      return item.pay_type === "Allowances";
                    })
                    .map((row) => {
                      return (
                        <option key={row.id} value={row.name}>
                          {row.name}
                        </option>
                      );
                    })}
                </NativeSelect>
              </div>
              <div>
                <InputLabel
                  htmlFor="deduct-customized-native-simple"
                  className={classes.formLabel}
                >
                  Allowance
                </InputLabel>
                <NativeSelect
                  name="allows_type4"
                  value={state.allows_type4}
                  style={{
                    padding: 4,
                    marginLeft: 5,
                    width: "100%",
                    textAlign: "left",
                  }}
                  onChange={handleChange}
                >
                  <option value="">None</option>
                  {payitems
                    .filter(function (item) {
                      return item.pay_type === "Allowances";
                    })
                    .map((row) => {
                      return (
                        <option key={row.id} value={row.name}>
                          {row.name}
                        </option>
                      );
                    })}
                </NativeSelect>
              </div>
              <div>
                <InputLabel
                  htmlFor="deduct-customized-native-simple"
                  className={classes.formLabel}
                >
                  Allowance
                </InputLabel>
                <NativeSelect
                  name="allows_type5"
                  value={state.allows_type5}
                  style={{
                    padding: 4,
                    marginLeft: 5,
                    width: "100%",
                    textAlign: "left",
                  }}
                  onChange={handleChange}
                >
                  <option value="">None</option>
                  {payitems
                    .filter(function (item) {
                      return item.pay_type === "Allowances";
                    })
                    .map((row) => {
                      return (
                        <option key={row.id} value={row.name}>
                          {row.name}
                        </option>
                      );
                    })}
                </NativeSelect>
              </div>
              <div>
                <InputLabel
                  htmlFor="deduct-customized-native-simple"
                  className={classes.formLabel}
                >
                  Allowance
                </InputLabel>
                <NativeSelect
                  name="allows_type6"
                  value={state.allows_type6}
                  style={{
                    padding: 4,
                    marginLeft: 5,
                    width: "100%",
                    textAlign: "left",
                  }}
                  onChange={handleChange}
                >
                  <option value="">None</option>
                  {payitems
                    .filter(function (item) {
