import React, { useState, useEffect, useCallback } from "react";
import { useHistory, Redirect } from "react-router-dom";
import { Button, ButtonGroup, TextField } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useCustomToast } from "../helpers/useCustomToast";
import { useEmployees } from "./employees/useEmployees";
import { useRecoilState } from "recoil";
import { loginLevelState } from "./data/atomdata";
import { useEmployeesContext } from "../context/employees_context";
import { setStoredUser } from "./user-storage";
import { useUser } from "./user/useUser";
import { useAuthContext } from "../context/auth_context";
import App from "../utils/firebase";

const SigninForm = () => {
  let history = useHistory();
  const classes = useStyles();
   const toast = useCustomToast();
  const { currentUser } = useAuthContext();
  const { employees } = useEmployees();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");
  const [alert, setAlert] = useState(true);
  const [error, setError] = useState(false);
  //const [isLoading, setIsLoading] = useState(false);

  const [loginLevel, setLoginLevel] = useRecoilState(loginLevelState);
  const {
    loadEmployees,
    setEditEmployeeID,
    employees_loading,
    employees_error,
  } = useEmployeesContext();

  // useEffect(() => {
  //   loadEmployees();
  // }, []);

  const handleLogin = useCallback(async (event) => {
    event.preventDefault();
    console.log("sigin", password, email)
    //const { email, password } = event.target.elements;
    try {
      await App.auth().signInWithEmailAndPassword(email, password);
      //history.pushState("/");
      toast({
        title: "Login in successfully!",
        status: "success",
      });
    } catch (error) {
       toast({
         title: "Invalid email or password!",
         status: "warning",
       });
    }
  },[]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const emp = employees
      .filter((item) => item.email === email)
      .map((row) => {
        return {
          id: row.id,
          name: row.name,
          email: row.email,
          password: row.password,
          role: role,
          leave_bal: row.leave_bal,
          siteallows_fee: row.siteallows_fee,
          perdiem_fee: row.perdiem_fee,
          reporting_to: row.reporting_to,
        };
      });
    console.log("emp", emp, emp.length);
    if (!emp.length) {
      setError(true);
      setTimeout(() => {
        setError(false);
      }, 2000);
    } else {
      if (emp[0].email === email && emp[0].password === password) {
        setLoginLevel({
          ...loginLevel,
          loginUser: emp[0].name,
          loginUserId: emp[0].id,
          loginLevel: role,
          loginEmail: email,
          login: true,
          leave_bal: emp[0].leave_bal,
          siteallows_fee: emp[0].siteallows_fee,
          perdiem_fee: emp[0].perdiem_fee,
          reporting_to: emp[0].reporting_to,
        });
        setEditEmployeeID(emp[0].id);
        setPassword("");
        setStoredUser(emp[0]);

        console.log("save user", loginLevel);
      } else {
        setAlert(false);
        setTimeout(() => {
          setAlert(true);
        }, 2000);
      }
    }
  };

  const handleStaffClick = (e) => {
    console.log("Staff");
    e.preventDefault();
    setRole("Staff");
    handleSubmit(e);
  };
  const handleAdminClick = (e) => {
    e.preventDefault();
    setRole("Admin");
    handleSubmit(e);
  };
  const handleAdminManagerClick = (e) => {
    e.preventDefault();
    setRole("AdminManager");
    handleSubmit(e);
  };
  const handleManagerClick = (e) => {
    e.preventDefault();
    setRole("Manager");
    handleSubmit(e);
  };

  // if (currentUser) {
  //   return <Redirect to="/home" />;
  // }
  return (
    <form className={classes.root} onSubmit={handleLogin}>
      <div style={{ textAlign: "center" }}>
        <h2>You are currently login: {currentUser}</h2>
        <TextField
          label="Email"
          variant="filled"
          type="email"
          required
          style={{ width: 350 }}
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <TextField
          label="Password"
          variant="filled"
          type="password"
          style={{ width: 350 }}
          required
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>

      <div style={{ textAlign: "center" }}>
        {/* <ButtonGroup> */}
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className={classes.button}
          onClick={(e) => setRole("Staff")}
        >
          Staff
        </Button>
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className={classes.button}
          onClick={(e) => setRole("Admin")}
        >
          Admin
        </Button>
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className={classes.button}
          onClick={(e) => setRole("AdminManager")}
        >
          Admin Manager
        </Button>
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className={classes.button}
          onClick={(e) => setRole("Manager")}
        >
          Manager
        </Button>
        {/* </ButtonGroup> */}
      </div>
      {!alert && <h3>Login Fail!</h3>}
      {error && <h3>Email not found!</h3>}
    </form>
  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    padding: theme.spacing(2),

    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: "600px",
    },
    "& .MuiButtonBase-root": {
      margin: theme.spacing(2),
    },
  },
}));

export default SigninForm;
