  //     // eslint-disable-next-line no-fallthrough
  //     default:
  //       lAccess = false;
  //   }
  // };
  // if ((lAccess = false)) {
  //   // eslint-disable-next-line no-undef
  //   setAccess(false);
  //   setTimeout(() => {
  //     setAccess(true);
  //   }, 3000);
  // } else {
  //   setLoginLevel({
  //     ...loginLevel,
  //     loginUser: emp[0].name,
  //     loginUserId: emp[0].id,
  //     loginLevel: role,
