import React, { useState, useEffect } from "react";
import getFirebase from "../utils/firebase";
import { Button, ButtonGroup } from "@material-ui/core"
import { makeStyles } from "@material-ui/core/styles";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
  useSetRecoilState,
} from "recoil";

import LoginForm from "./LoginForm";
import DashboardMain from "./DashboardMain";
import DashboardStaff from "./DashboardStaff";
import DashboardAdmin from "./DashboardAdmin";
import DashboardAdminManager from "./DashboardAdminManager";
import DashboardManager from "./DashboardManager";
import { loginLevelState } from "./data/atomdata";
import { useEmployeesContext } from "../context/employees_context";

const Main = () => {
  const classes = useStyles();
  const [currentUser, setCurrentUser] = useState(null);
  const [loginLevel, setLoginLevel] = useRecoilState(loginLevelState);
   const [role, setRole] = useState("");
  const {
    single_employee,
    single_employee_loading,
    single_employee_error,
    getSingleEmployeeEmail,
  } = useEmployeesContext();

  useEffect(() => {
    const firebase = getFirebase();

    if (firebase) {
      firebase.auth().onAuthStateChanged((authUser) => {
        if (authUser) {
          setCurrentUser(authUser.email);
        } else {
          setCurrentUser(null);
        }
      });
    }
  }, []);

  const SwitchCase = () => {
    //console.log(loginLevel);
    switch (loginLevel.loginLevel) {
      case "Staff":
        return <DashboardStaff />;
      case "Admin":
        return <DashboardAdmin />;
      case "AdminManager":
        return <DashboardAdminManager />;
      case "Manager":
        return <DashboardManager />;
      default:
        return "You are not authorised user!";
    }
  };

  if (!currentUser) {
    return (
      <LoginForm currentUser={currentUser} setCurrentUser={setCurrentUser} />
    );
  }

  return (
    <div style={{ textAlign: "center" }}>
      <form>
        
      </form>
      <ButtonGroup>
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className={classes.button}
          onClick={(e) => setRole("Staff")}
        >
          Staff
        </Button>
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className={classes.button}
          onClick={(e) => setRole("Admin")}
        >
          Admin
        </Button>
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className={classes.button}
          onClick={(e) => setRole("AdminManager")}
        >
          Admin Manager
        </Button>
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className={classes.button}
          onClick={(e) => setRole("Manager")}
        >
          Manager
        </Button>
      </ButtonGroup>
    </div>
    // <div>
    //   <SwitchCase />
    // </div>
  );
};

const useStyles = makeStyles((theme) => ({
  app: {
    height: "100vh",
    width: "500",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  root: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    padding: theme.spacing(2),

    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: "300px",
    },
    "& .MuiButtonBase-root": {
      margin: theme.spacing(2),
    },
  },
  card2: {
    position: "relative",
    width: "400px",
    color: "primary",
    //borderStyle: "solid",
    //borderColor: "blue",
    boxShadow:
      "rgba(255, 0, 0, 0.117647) 0px 1px 6px, rgba(255, 0, 0, 0.117647) 0px 1px 4px",
  },
  cardHeader: {
    display: "flex",
    paddingBottom: "10px",
    alignItems: "center",
    justifyContent: "center",
  },
  typography: {
    flexGrow: 1,
    align: "center",
  },
  media2: {
    margin: "-70px auto 0",
    width: "80%",
    height: 300,
    borderRadius: "4px",
    boxShadow: "0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23)",
    position: "relative",
    zIndex: 1000,
    paddingTop: "56.25%",
  },
  card: {
    width: 650,
    margin: "auto",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
  },
  media: {
    height: "800",
    width: "100%",
    objectFit: "cover",
    // marginLeft: '33%'
    // paddingTop: "56.25%", // 16:9
  },
  divider: {
    // Theme Color, or use css color in quote
    background: theme.palette.divider,
    padding: 4,
  },
  img: {
    maxWidth: "100%",
    height: "auto",
  },
}));

export default Main;
