import React, { useState } from "react";
import {
  Button,
  Icon,
  TextField,
  Paper,
  Typography,
  Grid,
  Divider,
  Checkbox,
  FormControlLabel,
} from "@material-ui/core";

import { Alert } from "@material-ui/lab";
import { makeStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import { useEmployeesContext } from "../context/employees_context";
import { Controller, useForm } from "react-hook-form";
import { useRecoilState } from "recoil";
import { loginLevelState } from "./data/atomdata";
import { useAddEmployees } from "./employees/useAddEmployees";
import { useUpdateEmployees } from "./employees/useUpdateEmployees";
import { useDepartments } from "./departments/useDepartments";
import { useDesignations } from "./designations/useDesignations";
import App from "../utils/firebase";

// import EmpFamily from "./EmpFamily";
// import EmpEducations from "./EmpEducations";
// import EmpExperiences from "./EmpExperiences";
// import EmpTrainings from "./EmpTrainings";

const initial_values = {
  name: "",
  gender: "",
  ic_no: "",
  email: "",
  age: 0,
  birthdate: null,
  address: "",
  nationality: "",
  basic_salary: 0,
  currency: "BND",
  bank_name: "",
  bank_acno: "",
  tap_checkbox: true,
  tap_acno: "",
  scp_acno: "",
  date_of_join: null,
  date_of_resign: null,
  leave_bal:0,
  leave_entitled:0,
  designation: "",
  department: "",
  passportno: "",
  passport_expirydate: null,
  workpermitno: "",
  workpermit_expirydate: null,
  siteallows_fee: 0,
  perdiem_fee: 0,
  role: 1,
  password: "abc123*",
  empno: "",
};

const EmployeeFormNew = () => {
  const classes = useStyles();
  const {
    isEditing,
    single_employee,
    updateEmployee,
    //addEmployee,
    editEmployeeID,
    single_employee_loading,
  } = useEmployeesContext();
  const {
    name,
    ic_no,
    gender,
    age,
    birthdate,
    email,
    address,
    nationality,
    basic_salary,
    currency,
    bank_name,
    bank_acno,
    tap_checkbox,
    tap_acno,
    scp_acno,
    leave_bal,
    leave_entitled,
    password,
    date_of_join,
    date_of_resign,
    designation,
    department,
    passportno,
    passport_expirydate,
    workpermitno,
    workpermit_expirydate,
    siteallows_fee,
    perdiem_fee,
  } = single_employee || initial_values;
  const addEmployees = useAddEmployees();
  const updateEmployees = useUpdateEmployees();
  const { designations } = useDesignations();
  const { departments } = useDepartments();
  //const [alert, setAlert] = useState(false);
  const { handleSubmit, control } = useForm();
  const [loginLevel, setLoginLevel] = useRecoilState(loginLevelState);
  console.log("emplevel", loginLevel);
