import React from "react";
import { Bar, char } from "react-chartjs-2";
import { Box } from "@chakra-ui/react";

const data = {
  labels: ["January", "February", "March", "April", "May", "June", "July","August","September","October","November","December"],
  datasets: [
    {
      label: "Basic Salary",
      backgroundColor: "blue",
      borderColor: "rgba(255,99,132,1)",
      borderWidth: 1,
      stack: 1,
      hoverBackgroundColor: "rgba(255,99,132,0.4)",
      hoverBorderColor: "rgba(255,99,132,1)",
      data: [65, 59, 80, 81, 56, 55, 40,35,50,65,],
    },
    {
      label: "Allowances",
      backgroundColor: "green",
      borderColor: "rgba(255,99,132,1)",
      borderWidth: 1,
      stack: 1,
      hoverBackgroundColor: "rgba(255,99,132,0.4)",
      hoverBorderColor: "rgba(255,99,132,1)",
      data: [45, 79, 10, 41, 16, 85, 20,25,23],
    },
    {
      label: "Deductions",
      backgroundColor: "red",
      borderColor: "rgba(255,99,132,1)",
      borderWidth: 1,
      stack: 1,
      hoverBackgroundColor: "rgba(255,99,132,0.4)",
      hoverBorderColor: "rgba(255,99,132,1)",
      data: [45, 79, 10, 41, 16, 85, 20,16,25,35],
    },
  ],
};

export default function BarChartStack() {
  const options = {
    responsive: true,
    legend: {
      display: false,
    },
    type: "bar",
    scales: {
      xAxes: [
        {
          stacked: true,
        },
      ],
      yAxes: [
        {
          stacked: true,
        },
      ],
    },
  };
  return (
    <Box>
      <Bar data={data} width={1400} height="900" options={options} />
    </Box>
  );
}
