import React, { useState, useRef } from "react";
import PropTypes from "prop-types";
import {
  AlertDialog,
  AlertDialogBody,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogContent,
  AlertDialogOverlay,
  Button,
  Heading,
} from "@chakra-ui/react";

export const AlertDialogBox = ({ isOpen, onClose, onConfirm  ,cancelRef, title, children }) => {
  return (
    <AlertDialog
      isOpen={isOpen}
      cancelRef={cancelRef}
      onClose={onClose}
      conConfirm={onConfirm}
    >
      <AlertDialogOverlay>
        <AlertDialogContent>
          <AlertDialogHeader fontSize="lg" fontWeight="bold">
            <Heading size="md">{title}</Heading>
          </AlertDialogHeader>

          <AlertDialogBody><Heading size="md">{children}</Heading></AlertDialogBody>

          <AlertDialogFooter>
            {/* <Button ref={cancelRef} onClick={onClose}> */}
            <Button onClick={onClose}>Cancel</Button>
            <Button colorScheme="red"
              onClick={() => {
                onClose()
                onConfirm()
              }}
              ml={3}>
              Confirm
            </Button>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialogOverlay>
    </AlertDialog>
  );
};

