                    id="margin-normal1"
                    name="findings"
                    defaultValue={formdata.findings}
                    className={classes.textField}
                    onChange={onChange}
                    error={!!error}
                    helperText={error ? error.message : null}
                    InputProps={{
                      readOnly: true,
                    }}
                  ></TextField>
                );
              }}
            />
          </div>
