import React, { useState, useEffect } from "react";
import {
  Button,
  ButtonGroup,
  Grid,
  Icon,
  TextField,
  FormControlLabel,
  Divider,
  ListSubheader,
  MenuItem,
  Select,
  NativeSelect,
  InputLabel,
} from "@material-ui/core";
import { Box, Checkbox, Heading, Text } from "@chakra-ui/react";
import currency from "currency.js";
import CurrencyTextField from "@unicef/material-ui-currency-textfield";
import { Autocomplete } from "@material-ui/lab";
import { makeStyles } from "@material-ui/core/styles";
import { usePayslipsContext } from "../context/payslips_context";

const initial_state = [
  {
    name: "",
    period: "",
    date: "",
    payrun: "",
    nett_pay: 0,
    bank_name: "",
    bank_acno: "",
    tap_acno: "",
    tap_amount: 0,
    scp_acno: "",
    scp_amount: 0,
    total_allowances: 0,
    total_deductions: 0,
    empid: "",
    status: "",
    allows_type1: " ",
    allows_type1amt: 0,
    allows_type2: " ",
    allows_type2amt: 0,
    allows_type3: " ",
    allows_type3amt: 0,
    allows_type4: " ",
    allows_type4amt: 0,
    allows_type5: " ",
    allows_type5amt: 0,
    allows_type6: " ",
    allows_type6amt: 0,
    allows_type7: "",
    allows_type7amt: 0,
    allows_type8: "",
    allows_type8amt: 0,
    deducts_type1: "",
    deducts_type1amt: 0,
    deducts_type2: " ",
    deducts_type2amt: 0,
    deducts_type3: " ",
    deducts_type3amt: 0,
    deducts_type4: "",
    deducts_type4amt: 0,
    deducts_type5: "",
    deducts_type5amt: 0,
    deducts_type6: "",
    deducts_type6amt: 0,
    deducts_type7: "",
    deducts_type7amt: 0,
    deducts_type8: "",
    deducts_type8amt: 0,
    tap_checkbox: false,
    salary_currency: "",
    currency_rate: 1,
    nett_pay_bnd: 0,
    wages_bnd: 0,
    site_allows_bnd: 0,
    expenses_claims_bnd: 0,
    tap_amount_bnd: 0,
    scp_amount_bnd: 0,
    total_allowances_bnd: 0,
    total_deductions_bnd: 0,
  },
];

const PayForm = ({
  formdata,
  setFormdata,
  loadFormdata,
  setLoadFormdata,
  payitems,
  setLoadUpdatedata,
  rowindex,
  isCalc,
  setIsCalc,
}) => {
  const classes = useStyles();
  const [state, setState] = useState(initial_state);
  const { singlebatchpayslip } = usePayslipsContext();
  console.log("formdata", formdata);
  useEffect(() => {
    setState(initial_state);
    setState({ ...formdata });
    setLoadFormdata(false);
    console.log("useeffect state", state);
  }, [loadFormdata]);

  // useEffect(() => {
  //   handleCalc()
  // },[state])

  const Update_Empdata = ({ name, value }) => {
    let data = singlebatchpayslip[rowindex];
    data[name] = value;
    handle
    //console.log("update data", data);
    //console.log("update payslip", singlebatchpayslip[rowindex]);
  };

  const handleChange = (e) => {
    e.preventDefault();
    const { name, type, value } = e.target;
    const val = type === "number" ? parseFloat(value) : value;
    //setFormInput({ [name]: val });
    setState({ ...state, [name]: val });
    Update_Empdata({ name: name, value: val });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log("submitform");
    handleCalc();
  };

  const handleCalc = (e) => {
    var totalTAP = 0,
      totalSCP = 0,
      nettPay = 0.0,
      allows = 0,
      deducts = 0,
      siteallows = 0,
      expsclaims = 0,
      wagesbnd = 0,
      totalTAPbnd = 0,
      totalSCPbnd = 0,
      nettPaybnd = 0,
      allowsbnd = 0,
      deductsbnd = 0,
      siteallowsbnd = 0,
      expsclaimsbnd = 0;
    let data = singlebatchpayslip[rowindex];
    totalTAP = state.tap_checkbox ? Math.ceil(state.wages * 0.05) : 0;
    totalSCP = state.tap_checkbox
      ? Math.round((state.wages + Number.EPSILON) * 0.035 * 100) / 100
      : 0;
    if (totalSCP > 98) {
      totalSCP = 98;
    }
    siteallows = parseFloat(state.allows_type1amt);
    expsclaims = parseFloat(state.allows_type2amt);
    allows =
      parseFloat(state.allows_type3amt) +
      parseFloat(state.allows_type4amt) +
      parseFloat(state.allows_type5amt) +
      parseFloat(state.allows_type6amt) +
      parseFloat(state.allows_type7amt) +
      parseFloat(state.allows_type8amt);

    deducts =
      parseFloat(state.deducts_type1amt) +
      parseFloat(state.deducts_type2amt) +
      parseFloat(state.deducts_type3amt) +
      parseFloat(state.deducts_type4amt) +
      parseFloat(state.deducts_type5amt) +
      parseFloat(state.deducts_type6amt) +
      parseFloat(state.deducts_type7amt) +
      parseFloat(state.deducts_type8amt);

    nettPay =
      state.wages +
      siteallows +
      expsclaims -
      totalTAP -
      totalSCP +
      allows -
      deducts;

    wagesbnd =
      Math.round((state.wages + Number.EPSILON) * state.currency_rate * 100) /
      100;
    allowsbnd =
      Math.round((allows + Number.EPSILON) * state.currency_rate * 100) / 100;
    deductsbnd =
      Math.round((deducts + Number.EPSILON) * state.currency_rate * 100) / 100;
    totalTAPbnd =
      Math.round((totalTAP + Number.EPSILON) * state.currency_rate * 100) / 100;
    totalSCPbnd =
      Math.round((totalSCP + Number.EPSILON) * state.currency_rate * 100) / 100;
    siteallowsbnd =
      Math.round((siteallows + Number.EPSILON) * state.currency_rate * 100) /
      100;
    expsclaimsbnd =
      Math.round((expsclaims + Number.EPSILON) * state.currency_rate * 100) /
      100;
    nettPaybnd =
      Math.round((nettPay + Number.EPSILON) * state.currency_rate * 100) / 100;

    setState({
      ...state,
      total_allowances: allows,
      total_deductions: deducts,
      tap_amount: totalTAP,
      scp_amount: totalSCP,
      nett_pay: nettPay,
      wages_bnd: wagesbnd,
      total_allowances_bnd: allowsbnd,
      total_deductions_bnd: deductsbnd,
      tap_amount_bnd: totalTAPbnd,
      scp_amount_bnd: totalSCPbnd,
      site_allows_bnd: siteallowsbnd,
      expenses_claims_bnd: expsclaimsbnd,
      nett_pay_bnd: nettPaybnd,
    });
    //update employee data
    data.tap_amount = totalTAP;
    data.scp_amount = totalSCP;
    data.total_allowances = allows;
    data.total_deductions = deducts;
    data.nett_pay = nettPay;
    data.wages_bnd = wagesbnd;
  };

  return (
    <form onSubmit={handleFormSubmit}>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Text as="u" fontSize="md">
            Allowances
          </Text>
        </Grid>

        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Text as="u" fontSize="md">
            Deductions
          </Text>
        </Grid>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Text as="u" fontSize="md">
            Summary ({state.name})
          </Text>
        </Grid>
      </Grid>
      <Grid container direction="row" style={{ border: "1px solid white" }}>
        <Grid item sm={4} align="center" style={{ border: "1px solid white" }}>
          <Grid container direction="row" style={{ border: "1px solid white" }}>
            <Grid
              item
              sm={8}
              align="center"
              style={{ border: "1px solid white" }}
            >
              <Box>
