                    type="number"
                    value={currency(state.total_allowances_bnd)}
                    onChange={handleChange}
                    style={{ width: "100%" }}
                    InputLabelProps={{
                      shrink: true,
                    }}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                </div>
                <div>
                  <TextField
                    label="Total Deductions"
                    name="total_deductions"
                    variant="filled"
                    type="number"
                    value={currency(state.total_deductions_bnd)}
                    onChange={handleChange}
                    style={{ width: "100%" }}
                    InputLabelProps={{
                      shrink: true,
                    }}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                </div>
                <div>
                  <TextField
                    label="Nett Pay"
                    name="nett_pay"
                    variant="filled"
                    type="number"
                    value={currency(state.nett_pay_bnd)}
                    onChange={handleChange}
                    style={{ width: "100%" }}
                    InputLabelProps={{
                      shrink: true,
                    }}
                    InputProps={{
                      readOnly: true,
                    }}
                  />
                </div>
              </Grid>
            )}
          </Grid>
        </Grid>
        {/* <button>Submit</button> */}
      </Grid>
    </form>
  );
};

