import React, { lazy, Suspense, useState, useEffect } from "react";
import getFirebase from "../utils/firebase";
import { Button, ButtonGroup } from "@material-ui/core";
import { getUser, getToken } from "../helpers/storageHelper";
import { makeStyles } from "@material-ui/core/styles";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
  useSetRecoilState,
} from "recoil";

import LoginForm from "./LoginForm";
import { AlertShutDownDialog } from "../helpers/AlertShutDownDialog";
import DashboardStaff from "./DashboardStaff";
import DashboardAdmin from "./DashboardAdmin";
import DashboardAdminManager from "./DashboardAdminManager";
import DashboardManager from "./DashboardManager";
import { loginLevelState } from "./data/atomdata";
import { useEmployeesContext } from "../context/employees_context";

//const DashboardAdmin = lazy(() => import('./DashboardAdmin'));
//const DashboardStaff = lazy(() => import('./DashboardStaff'));
//const DashboardAdminManager = lazy(() => import('./DashboardAdminManager'));
//const DashboardManager = lazy(() => import("./DashboardManager"));

const Main = () => {
  const classes = useStyles();
  const [currentUser, setCurrentUser] = useState(null);
  const firebaseInstance = getFirebase();
  const [loginLevel, setLoginLevel] = useRecoilState(loginLevelState);
  const [role, setRole] = useState("");
  const [access, setAccess] = useState(true);
  const {
    employees,
    employees_loading,
    loadEmployees,
    setEditEmployeeID,
    employees_error,
    single_employee,
    single_employee_loading,
    single_employee_error,
    getSingleEmployeeEmail,
  } = useEmployeesContext();

  useEffect(() => {
    const firebase = getFirebase();

    if (firebase) {
      firebase.auth().onAuthStateChanged((authUser) => {
        if (authUser) {
          setCurrentUser(authUser.email);
          //const user = getUser();
          //console.log("firebase", user);
        } else {
          setCurrentUser(null);
        }
      });
    }
  }, []);

  // useEffect(() => {
  //   loadEmployees();
  // }, []);

  const handleLogout = async (e) => {
    e.preventDefault();
    try {
      if (firebaseInstance) {
        await firebaseInstance.auth().signOut();
        alert("Successfully signed out!");
        console.log("logout");
      }
    } catch (error) {
      console.log("error", error);
    }
  };

  const SwitchCase = () => {
    console.log("loginlevel", role);
    switch (role) {
      case "Staff":
        return <DashboardStaff />;
      case "Admin":
        return <DashboardAdmin />;
      case "AdminManager":
        return <DashboardAdminManager />;
      case "Manager":
        return <DashboardManager />;
      default:
        return (
          <div>
            <button onClick={(e) => handleLogout(e)}>Logout</button>
          </div>
          //  firebaseInstance.auth().signOut()
          // <div>
          //   You are not authorised user! Pls logout!
          //   <button type="submit" onClick={(e) => handleLogout(e)}>
          //     Logout
          //   </button>
          // </div>
          // <LoginForm
          //   currentUser={currentUser}
          //   setCurrentUser={setCurrentUser}
          //   role={role}
          //   setRole={setRole}
          // />
        );
    }
  };

  // if (currentUser && !role) {
  //   return <div>firebaseInstance.auth().signOut(); setCurrentUser(null);</div>;
  // }

  if (!currentUser) {
    return <LoginForm role={role} setRole={setRole} />;
  }

  if (employees_loading) {
    return <div>Loading...</div>;
  }
  return (
    <div>
      <SwitchCase />
      {/* <AlertShutDownDialog /> */}
    </div>
  );
};

const useStyles = makeStyles((theme) => ({
  app: {
    height: "100vh",
    width: "500",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  root: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    padding: theme.spacing(2),

    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: "300px",
    },
    "& .MuiButtonBase-root": {
      margin: theme.spacing(2),
    },
  },
  card2: {
    position: "relative",
    width: "400px",
    color: "primary",
    //borderStyle: "solid",
    //borderColor: "blue",
    boxShadow:
      "rgba(255, 0, 0, 0.117647) 0px 1px 6px, rgba(255, 0, 0, 0.117647) 0px 1px 4px",
  },
  cardHeader: {
    display: "flex",
    paddingBottom: "10px",
    alignItems: "center",
    justifyContent: "center",
  },
  typography: {
    flexGrow: 1,
    align: "center",
  },
  media2: {
    margin: "-70px auto 0",
    width: "80%",
    height: 300,
    borderRadius: "4px",
    boxShadow: "0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23)",
    position: "relative",
    zIndex: 1000,
    paddingTop: "56.25%",
  },
  card: {
    width: 650,
    margin: "auto",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
  },
  media: {
    height: "800",
    width: "100%",
    objectFit: "cover",
    // marginLeft: '33%'
    // paddingTop: "56.25%", // 16:9
  },
  divider: {
    // Theme Color, or use css color in quote
    background: theme.palette.divider,
    padding: 4,
  },
  img: {
    maxWidth: "100%",
    height: "auto",
  },
}));

export default Main;
