sourcesContent:
import React, { useEffect } from "react";
import {
  Button,
  Icon,
  TextField,
  Paper,
  Typography,
  Grid,
  Divider,
} from "@material-ui/core";
//import { Alert, AlertTitle } from "@material-ui/lab";
import CurrencyTextField from "@unicef/material-ui-currency-textfield";
import { makeStyles } from "@material-ui/core/styles";
import MenuItem from "@material-ui/core/MenuItem";
import { useCustomToast } from "../helpers/useCustomToast";
import { selector, useRecoilState, useRecoilValueLoadable } from "recoil";
import { loginLevelState } from "./data/atomdata";
import { useEmployeesContext } from "../context/employees_context";
import { Controller, useForm } from "react-hook-form";

import { useEmployees } from "./employees/useEmployees";
import { useUpdateEmployees } from "./employees/useUpdateEmployees";
// import EmpFamilyStaff from "./EmpFamilyStaff";
// import EmpEducationsStaff from "./EmpEducationsStaff";
// import EmpExperiencesStaff from "./EmpExperiencesStaff";
import EmpFamily from "./EmpFamily";
import EmpEducations from "./EmpEducations";
import EmpExperiences from "./EmpExperiences";
import EmpTrainings from "./EmpTrainings";
import App from "../utils/firebase";

const initial_values = {
  name: "",
  birthdate: null,
  empno: "",
  gender: "",
  ic_no: "",
  email: "",
  age: 0,
  basic_salary: 0,
  salary_currency: "BND",
  bank_name: "",
  bank_acno: "",
  nationality: "",
  address: "",
  leave_bal: 0,
  leave_entitled: 0,
  tap_acno: "",
  scp_acno: "",
  date_of_join: null,
  date_of_resign: null,
  designation: "",
  department: "",
  passportno: "",
  passport_expirydate: null,
  workpermitno: "",
  workpermit_expirydate: null,
  siteallows_fee: 0,
  perdiem_fee: 0,
  reporting_to: "",
};

const EmployeeForm = () => {
  const classes = useStyles();
  const toast = useCustomToast();
  const { employees, setFilter } = useEmployees();
  const updateEmployees = useUpdateEmployees();
  const [empage, setEmpage] = useState(0);
  const [loginLevel, setLoginLevel] = useRecoilState(loginLevelState);
  const {
    isEditing,
    single_employee,
    editEmployeeID,
    getSingleEmployee,
    single_employee_loading,
  } = useEmployeesContext();

  const {
    name,
    empno,
    birthdate,
    ic_no,
    gender,
    age,
    email,
    nationality,
    address,
    basic_salary,
    salary_currency,
    bank_name,
    bank_acno,
    tap_acno,
    scp_acno,
    leave_bal,
    leave_entitled,
    date_of_join,
    date_of_resign,
    designation,
    department,
    passportno,
    passport_expirydate,
    workpermitno,
    workpermit_expirydate,
    siteallows_fee,
    perdiem_fee,
    reporting_to,
  } = single_employee || initial_values;
  const { handleSubmit, control } = useForm();

  const onSubmit = (data) => {
    updateEmployees({ id: editEmployeeID, ...data });
  };

  const Reset_PW = () => {
    try {
      App.auth().sendPasswordResetEmail(email);
      toast({
        title: `Reset Password sent to ${email}!`,
        status: "success",
      });
    } catch (error) {
      toast({
        title: `Fail to reset password on ${email}!`,
        status: "warning",
      });
      console.log(error);
    }
  };

  const calculateAge = (dob) => {
    var today = new Date();
    var birthDate = new Date(dob);
    var age = today.getFullYear() - birthDate.getFullYear();
    var m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }
    return age;
  };

  useEffect(() => {
    getSingleEmployee(editEmployeeID);
  }, []);

  useEffect(() => {
    let age = calculateAge(birthdate);
    setEmpage(age);
  }, []);

  if (single_employee_loading) {
    return (
      <div>
        <h2>Loading... </h2>
      </div>
    );
  }

  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        <Paper className={classes.root}>
          <Grid container className={classes.root} spacing={15}>
            <div>
              <Typography variant="h5" component="h3">
                EMPLOYEE FORM
              </Typography>

              <Button
                type="submit"
                variant="contained"
                color="primary"
                className={classes.button}
              >
                Submit <Icon className={classes.rightIcon}>send</Icon>
              </Button>
              <Button
                variant="contained"
                color="primary"
                className={classes.button}
                onClick={Reset_PW}
              >
                Reset PW <Icon className={classes.rightIcon}>send</Icon>
              </Button>
            </div>
          </Grid>
          <Divider className={classes.divider} />
          <Grid item xs={12}>
            <div>
              <div>
                <Controller
                  name="name"
                  control={control}
                  defaultValue={name}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Name"
                        id="standard-name"
                        name="name"
                        defaultValue={name}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  rules={{ required: "Name required" }}
                />
                <Controller
                  name="empno"
                  control={control}
                  defaultValue={empno}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Emp No"
                        id="standard-empno"
                        defaultValue={empno}
                        name="empno"
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  //rules={{ required: "Name required" }}
                />
              </div>
              <div>
                <Controller
                  name="email"
                  control={control}
                  defaultValue={email}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Email"
                        id="standard-email"
                        name="email"
                        defaultValue={email}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  rules={{ required: "Email is required" }}
                />

                <Controller
                  name="ic_no"
                  control={control}
                  defaultValue={ic_no}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="IC No"
                        id="standard-icno"
                        name="ic_no"
                        defaultValue={ic_no}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  //rules={{ required: "IC No required" }}
                />
              </div>
              <div>
                <Controller
                  name="birthdate"
                  control={control}
                  defaultValue={birthdate}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Birth Date"
                        id="standard-birthdate"
                        name="birthdate"
                        type="date"
                        defaultValue={birthdate}
                        className={classes.textField}
                        onChange={onChange}
                        error={!!error}
                        helperText={error ? error.message : null}
                        InputLabelProps={{
                          shrink: true,
                        }}
                      ></TextField>
                    );
                  }}
                  //rules={{ required: "IC No required" }}
                />

                <Controller
                  name="age"
                  control={control}
                  defaultValue={age}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
                  }) => {
                    return (
                      <TextField
                        label="Age"
                        type="number"
                        id="standard-age"
                        name="age"
                        defaultValue={age}
                        className={classes.textField}
                        //onChange={onChange}
                        onChange={(e) => {
                          onChange(parseInt(e.target.value, 10));
                        }}
                        error={!!error}
                        helperText={error ? error.message : null}
                      />
                    );
                  }}
                  //rules={{ required: "IC No required" }}
                />
              </div>
              <div>
                <Controller
                  name="passportno"
                  control={control}
                  defaultValue={passportno}
                  render={({
                    field: { onChange, value },
                    fieldState: { error },
